function run(input) {
  let oleCode = input.p_code ? input.p_code : [];
  let oleValue = input.p_value ? input.p_value : [];
  let dataLenght = input.codeLength;
  
  let newCode = [];
  let newValue = [];
  
  for(let i=0 ; i<dataLenght ; i++){
    if(newValue.indexOf(oleValue[i]) >= 0)
      continue;
      
    newCode.push(oleCode[i]);
    newValue.push(oleValue[i]);
  }

	return {
		newCode: newCode,
		newValue: newValue
	};
}
